declare module "index" {
	/// <reference types="node" />
	import { Server } from "http";
	interface VelocitousConfig {
		port: number;
		rootFolder: string;
		rewriteIndex: boolean;
	}
	class VelocitousServer {
		httpServer: Server;
		endpoints: any[];
		constructor(config: VelocitousConfig);
		endpoint(checker: Function, actor: Function): this;
		passthrough(checker: Function, target: string): void;
	}
	//export default VelocitousServer;
	export = VelocitousServer;
}
